from Maple_bot_functions.ExternalLibraries import *
from Maple_bot_functions.MapleDecorators import *

class Maple_help(commands.HelpCommand):
    def __init__(self):
        super().__init__()

    @bot_help
    async def send_bot_help(self, mapping, embed):
        await self.get_destination().send(embed = embed)

    @cog_help
    async def send_cog_help(self, cog, embed):
        await self.get_destination().send(embed = embed)

    async def send_group_help(self, group):
        await self.get_destination().send(
            f'{group.name}: ', {[command.name for command in group.commands]}
        )

    @command_help
    async def send_command_help(self, command, embed):
        await self.get_destination().send(embed=embed)